import {
	TRAER_TODAS,
	CARGANDO,
	ERROR,
	CAMBIO_USUARIO,
	CAMBIO_TITULO
} from '../types/tareasTypes';

const INITIAL_STATE = {
	tareas: {},
	cargando: false,
	error: '',
	usuario_id: '',
	titulo: ''
};

export default (state = INITIAL_STATE, action) => {
	switch (action.type) {
		case TRAER_TODAS:
			return {
				...state,
				tareas: action.payload,
				cargando: false,
				error: ''
			};

		case CARGANDO:
			return { ...state, cargando: true };

		case ERROR:
			return { ...state, error: action.payload, cargando: false };

		case CAMBIO_USUARIO:
			return { ...state, usuario_id: action.payload };

		case CAMBIO_TITULO:
			return { ...state, titulo: action.payload };
            // Justo después de la petición ejecuto un dispatch
            //  de tipo agregada, en el reducer creo ese caso que
            //   retona el estado y las tareas vacío porque ya no 
            //   están las mismas tareas se puso una nueva
		case 'agregada':
			return {
				...state,
				tareas: {},
				cargando: false,
				error: ''
			};

		default: return state;
	};
};

//ESTE TAREAS REDUCER  ES PARECIDO A USUARIOS REDUCER, DESPUES DE CREAR ESTE REDUCER
