import axios from 'axios';
import { TRAER_TODOS, CARGANDO, ERROR } from '../types/publicacionesTypes';

export const traerTodos = () => async(dispatch) => { 
    dispatch({
        type: CARGANDO
    });
    try {
    const respuesta = await axios.get('http://jsonplaceholder.typicode.com/posts')
dispatch ({
    type: TRAER_TODOS,
    payload: respuesta.data
})
}
catch (error) {
    console.log('Error:',error.message);
    dispatch({
        type: ERROR,
        payload: 'Algo Salio MAL! intenta mas tarde...' //este mensaje se va a ver reflejado cuando el request a la URL sale mal 
       })
    }
}
export const traerPorUsuario = (key) => async(dispatch, getState) => {
    const {usuarios} = getState().usuariosReducer;
    const usuarios_id = usuarios[key].id;
    const respuesta = await axios.get(`http://jsonplaceholder.typicode.com/posts?userId=${usuarios_id}`);

    dispatch ({
        type: 'TRAER_TODOS',
        payload: respuesta.data
    }) 

}
