import React, { Component } from 'react';
import axios from 'axios';

class Usuarios extends Component { //este es un componente funcional ya que empieza con const, el componente no funcional no manejan estados, solo manejan informacion 
constructor() {
  super();
  this.state = {
    usuarios: []
  }
}
async componentDidMount () { //en este didmount estamos cambiando el estado estamos cargando el arreglo de dos objetos.
  const respuesta = await axios.get('https://jsonplaceholder.typicode.com/users');
  this.setState({
    usuarios: respuesta.data
  })
}

ponerFilas = () => (
    this.state.usuarios.map((usuario) => (
<tr key={usuario.id}>
    <td>
    {usuario.name}
        </td>
      <td>
      {usuario.email}
        </td>
      <td>
      {usuario.website}
      </td>
</tr>
    ))
  );



      render() {
        console.log(this.state.usuarios);

        return (
          <div className="margen">
            <table className="tabla">
              <thead>
                <tr>
                  <th>
                    Nombre
                  </th>
                  <th>
                    Correo
                  </th>
                  <th>
                    Enlace
                  </th>
                </tr>
              </thead>
              <tbody>
                { this.ponerFilas() }
              </tbody>
            </table>
          </div>
        )
      }
}

export default Usuarios;
