import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as usuariosActions from '../../actions/usuariosActions'

//AL CREAR ESTE COMPONENTE NOS MOVEMOS A LA LINEA : 50 : en el Render
//esta es la clase usuario donde comienza todo. este es un componente clase
class Usuarios extends Component { //este es un componente funcional ya que empieza con const, el componente no funcional no manejan estados, solo manejan informacion 
//en este componente didmount no llamamos los usuarios, en vez de eso estamos llamando el action creator!! y el action creator! esta yendo a la URL y trayendo los usuarios y entregarselos al REDUCER!!

componentDidMount () { //en este didmount estamos cambiando el estado estamos cargando el arreglo de dos objetos.
  //ESTE ES EL ACTION CREATOR QUE VA A TRAER TODO, sabemos que es un action reator porque en la linea 66 lo estoy conectando con mi usuarios action
  //se manda a llamar todos de usuarios action {EL TRAER TODOS O VAMOS A ENCONTRAR EN EL ARCHIVO USUARIOSACTION.JS de la carpeta actions}
  this.props.traerTodos();
}

// este es un arreglo vacio el cual los usuarios se van a mostrar con los TR que se envioan desde la linea 50 a esta 
//En Usuarios No hay nada, es un Arreglo VACIO, asi lo define el reducer
// el componente aca empieza a llamar el ACTIONCREATOR, esto se realiza desde el DIDMOUNT, linea 8
ponerFilas = () => this.props.usuarios.map((usuario) => (
  <tr key={ usuario.id }>
    <td>
      { usuario.name }
    </td>
    <td>
      { usuario.email }
    </td>
    <td>
      { usuario.website }
    </td>
  </tr>
));
//todo lo de ponerFilas se cambia todo esto sucede cuando el payload: respuesta.data se actualiza con la nueva info y la trae de nmuevo


      render() {
        // console.log(this.props); aca en este concole log miramos como esta la distribucion de los objetos y componentes del prop 

        return (
          <div >
            <table className="tabla">
              <thead>
                <tr>
                  <th>
                    Nombre
                  </th>
                  <th>
                    Correo
                  </th>
                  <th>
                    Enlace
                  </th>
                </tr>
              </thead>
              <tbody> 
                { this.ponerFilas() } 
                {/* lo que hace este this.ponerFilas es que por cada usuario entrega una fila o un TR, mirar la linea 14 */}
              </tbody>
            </table>
          </div>
        )
      }
}

const mapStateToProps = (reducers) => { //le podemos entregar al componente el reducer que se requiera, en este caso solo los usuarios
return reducers.usuariosReducer;
}
export default connect(mapStateToProps, usuariosActions)(Usuarios);
