import React, { Component } from 'react';
import { connect } from 'react-redux';
import Spinner from '../general/Spinner';
import Fatal from '../general/Fatal';

import * as usuariosActions from '../../actions/usuariosActions';
import * as publicacionesActions from '../../actions/publicacionesActions';
//conectamos la informacion de usuarios reducer importando react-redux, conectamos este componente a redux 

const { traerTodos: usuariosTraerTodos } = usuariosActions;
const { traerPorUsuario: publicacionesTraerPorUsuario } = publicacionesActions;

class Publicaciones extends Component {
    async componentDidMount() { //esta parte se tradeuce asi : si no existen los usuarios entonces entonces se traen de usuarios action porque si nos vamos a otra pagina ellos deberian estar ahi 
        //osea que cuando regrese a usuarios no voy a necesitar traerlos porque ya existen en el reducer 
        const {
            usuariosTraerTodos,
            publicacionesTraerPorUsuario,
            match: {params: {key}}
        } = this.props;  //!!se saca todo esto del this.props para no colocarlo en los IF el codigo se reduce
        if (!this.props.usuariosReducer.usuarios.length) { //el estado es el suaurioReducer, este se actualiza poreso no lo sacamos y lo colocamos en la constante de arriba, se ejecuta una vez despues del render
             await usuariosTraerTodos();

        }
        if (this.props.usuariosReducer.error) {
			return;
		}
		if (!('publicaciones_key' in this.props.usuariosReducer.usuarios[key])) {
			await publicacionesTraerPorUsuario(key);
		}
    }

    ponerUsuario = () => {
		const {
			match: { params: { key } },
			usuariosReducer
		} = this.props;

		if (usuariosReducer.error) {
			return <Fatal mensaje={ usuariosReducer.error } />;
		}
		if (!usuariosReducer.usuarios.length || usuariosReducer.cargando) {
			return <Spinner />
		}

		const nombre = usuariosReducer.usuarios[key].name;

		return (
			<h1>
				Publicaciones de { nombre }
			</h1>
		);
    };
    //Conclusión: Creamos una función llamada ponerPublicaciones() 
    //que valida los estados de publicaciones (error y cargando)
    // y si todo sale bien ya tengo las publicaciones y de ese usuario 
    // destructuro publicaciones key (donde están las publicaciones de este usuario)
    //y retorno todas las publicaciones que están en esa casilla del arreglo con un .map
    //tanto lió para mostrar las publicaciones de un usuario pero igual excelente clase…
    ponerPublicaciones = () => {
		const {
			usuariosReducer,
			usuariosReducer: { usuarios },
			publicacionesReducer,
			publicacionesReducer: { publicaciones },
			match: { params: { key } }
		} = this.props;

		if (!usuarios.length) return;
		if (usuariosReducer.error) return;
		if (publicacionesReducer.cargando) {
			return <Spinner />;
		}
		if (publicacionesReducer.error) {
			return <Fatal mensaje={ publicacionesReducer.error } />
		}
		if (!publicaciones.length) return;
		if (!('publicaciones_key' in usuarios[key])) return;

		const { publicaciones_key } = usuarios[key];
		return publicaciones[publicaciones_key].map((publicacion) => (
			<div className='pub_titulo'>
				<h2>
					{ publicacion.title }
				</h2>
				<h3>
					{ publicacion.body }
				</h3>
			</div>
		));
	};

	render() {
		return (
			<div>
				{ this.ponerUsuario() }
				{ this.ponerPublicaciones() }
			</div>
		);
	}
}

const mapStateToProps = ({ usuariosReducer, publicacionesReducer }) => {
	return { usuariosReducer, publicacionesReducer };
};

const mapDispatchToProps = {
	usuariosTraerTodos,
	publicacionesTraerPorUsuario
};

export default connect(mapStateToProps, mapDispatchToProps)(Publicaciones);
