import React, { Component } from 'react';
import {connect} from 'react-redux';
import * as usuariosActions from '../../actions/usuariosActions';
import * as publicacionesActions from '../../actions/publicacionesActions';
//conectamos la informacion de usuarios reducer importando react-redux, conectamos este componente a redux 

const { traerTodos: usuariosTraerTodos } = usuariosActions;
const { traerPorUsuario: publicacionesTraerPorUsuario } = publicacionesActions;

class Publicaciones extends Component {
    async componentDidMount() { //esta parte se tradeuce asi : si no existen los usuarios entonces entonces se traen de usuarios action porque si nos vamos a otra pagina ellos deberian estar ahi 
        //osea que cuando regrese a usuarios no voy a necesitar traerlos porque ya existen en el reducer 
        if (!this.props.usuariosReducer.usuarios.length) {
             await this.props.usuariosTraerTodos();

        }
        if (!('publicciones_key' in this.props.usuariosReducer.usuarios[this.props.match.params.key])) {
            this.props.publicacionesTraerPorUsuario(this.props.match.params.key);
        }
    }
    render() {
        console.log(this.props);

        return (
            <div>
            <h1>
            Publicaciones de
            </h1>
            {this.props.match.params.key}  
            </div>
        )
    }
}

const mapStateToProps = ({ usuariosReducer, publicacionesReducer }) => {
	return { usuariosReducer, publicacionesReducer };
};

const mapDispatchToProps = {
	usuariosTraerTodos,
	publicacionesTraerPorUsuario
};

export default connect(mapStateToProps, mapDispatchToProps)(Publicaciones);
