import React, { Component } from 'react';

class App extends Component { //este es un componente funcional ya que empieza con const, el componente no funcional no manejan estados, solo manejan informacion 
constructor() {
  super();
  this.state = {
    usuarios: []
  }
}
componentDidMount () {
  this.setState({
    usuarios: [ 
      {
      nombre: 'Horacio',
      correo: 'Horacio.jgo@gmail.com',
      enlace: 'www.github.com'
      },
      {
        nombre: 'Horacio',
        correo: 'Horacio.jgo@gmail.com',
        enlace: 'www.github.com'
      }
    ]
  })
}
  ponerFilas = () => (
    this.state.usuarios.map((usuarios) => (
<tr>
    <td>
    {usuarios.nombre}
        </td>
      <td>
      {usuarios.correo}
        </td>
      <td>
      {usuarios.enlace}
      </td>
</tr>
    ))
  );



      render() {
        return (
          <div className="margen">
            <table className="tabla">
              <thead>
                <tr>
                  <th>
                    Nombre
                  </th>
                  <th>
                    Correo
                  </th>
                  <th>
                    Enlace
                  </th>
                </tr>
              </thead>
              <tbody>
                { this.ponerFilas() }
              </tbody>
            </table>
          </div>
        )
      }
}

export default App;
