import { TRAER_POR_USUARIO, CARGANDO, ERROR } from '../types/publicacionesTypes';

const INITIAL_STATE = {
	publicaciones: [],
	cargando: false,
	error: ''
};

export default (state = INITIAL_STATE, action) => {
	switch (action.type) {
		case TRAER_POR_USUARIO: //traertodos sobreescribe las publicaciones para que se cree un nuevo estado
			return { ...state, 
                publicaciones: action.payload,
                cargando: false, // aca lo coloca en falso despues de que el estado cambia en el render vuelve aca para verificar que no esta cargando y se coloca en falso 
                error: ''
            } 

            case CARGANDO:
                return { ...state, cargando: true };

            case ERROR:
                return { ...state, error: action.payload, cargando: false };


		default: return state;
	};
};
