import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './components/App';
import { createStore } from 'redux';
import { Provider } from 'react-redux';
import reducers from './reducers'
//despues de tener importado e isntalado redux en el proyecto debemos crear el store 
//El store tiene las siguientes responsabilidades:

// Contiene el estado de la aplicación
// Permite el acceso al estado vía getState()
// Permite que el estado sea actualizado vía dispatch(action)
// Registra los listeners vía subscribe(listener)
// Maneja la anuliación del registro de los listeners via el retorno de la función de subscribe(listener)

const store = createStore( //todo lo que esta adentro de esta constante la APP abajo va a tener acceso a ella, y va a poder manejar todos los estados con el provider de redux 
   reducers, //todos los reducers
  {} //estado inicial
)

ReactDOM.render(
  <Provider store={store}>
    <App />,
  </Provider>,
  document.getElementById('root')
);
