import axios from 'axios';
import { TRAER_TODOS, CARGANDO, ERROR } from '../types/usuariosTypes';

export const traerTodos= () => async (dispatch) => { //este dispatch contacta al reducer para que haga el cambio de estado
    dispatch({
        type: CARGANDO
    });
  try {
    const respuesta = await axios.get('https://jsonplaceholder.typicode.com/userss'); 

    //aca estamos realizando la peticion HTTP LA URL que es un GET una vez realizado el get se realiza el dispatch 
    //dispatch del typo trer usuarios, y le voy a entregar al reducer los datos con respuesta.data
    //este dispatch es el que se comunica con el REDUCER.
    //En teoria dice lo siguiente: REDUCER => Te entrego el siguiente Caso=> traer usuarios de aca pasamos a UsuariosReducer que esta en el la carpeta REDUCERS.
    dispatch({
        type: TRAER_TODOS, //este traer todos esta en el archivo usuariosType lo cambiamos para que no genere errores de tipografia, es opcional
        payload: respuesta.data //este data son los usuarios de la URL  del archivo jsonplaceholder desde usuarios Reducer esto cambia y lo envia al componente y modifica el estado alla osea en el archivo index.js en la carpeta usuarios linea . 
    })
  }
  catch (error) {
